DROP VIEW IF EXISTS `author_picture`;
CREATE ALGORITHM=UNDEFINED SQL SECURITY DEFINER VIEW `author_picture` AS select `a`.`id` AS `id`,group_concat(distinct `p`.`path` separator ',') AS `picture` from ((`author` `a` join `item_picture` `i_p` on(((`a`.`id` = `i_p`.`item_id`) and (`i_p`.`table` = 'author')))) join `picture` `p` on((`i_p`.`picture_id` = `p`.`id`))) group by `a`.`id` order by `a`.`id` ;

-- ----------------------------
-- View structure for `product_select`
-- ----------------------------
DROP VIEW IF EXISTS `product_select`;
CREATE ALGORITHM=UNDEFINED SQL SECURITY DEFINER VIEW `product_select` AS select `p`.`id` AS `p_id`,`p`.`price` AS `price`,group_concat(distinct concat_ws(' ',`a`.`name`,`a`.`surname`) separator ',') AS `full_name`,`b`.`name` AS `name`,`b`.`year` AS `year`,`b`.`available` AS `available`,`b`.`isbn` AS `isbn`,`b`.`description` AS `description`,group_concat(distinct `c`.`name` separator ',') AS `category`,group_concat(distinct `pic`.`path` separator ',') AS `picture`,`pub`.`name` AS `publisher` from (((((((((`product` `p` join `book` `b` on((`p`.`item_id` = `b`.`id`))) join `product_author` `p_a` on((`p`.`id` = `p_a`.`prod_id`))) join `author` `a` on((`p_a`.`author_id` = `a`.`id`))) join `product_category` `p_c` on((`p`.`id` = `p_c`.`prod_id`))) join `category` `c` on((`p_c`.`category_id` = `c`.`id`))) join `product_publisher` `p_pub` on((`p`.`id` = `p_pub`.`prod_id`))) join `publisher` `pub` on((`p_pub`.`publisher_id` = `pub`.`id`))) join `product_picture` `p_pic` on((`p`.`id` = `p_pic`.`prod_id`))) join `picture` `pic` on((`p_pic`.`picture_id` = `pic`.`id`))) group by `p`.`item_id` order by `p`.`item_id` desc ;

