CREATE ALGORITHM=UNDEFINED SQL SECURITY DEFINER VIEW `publisher_picture` AS select `pub`.`id` AS `id`,group_concat(distinct `p`.`path` separator ',') AS `picture` from ((`publisher` `pub` join `item_picture` `i_p` on(((`pub`.`id` = `i_p`.`item_id`) and (`i_p`.`table` = 'publisher')))) join `picture` `p` on((`i_p`.`picture_id` = `p`.`id`))) group by `pub`.`id` order by `pub`.`id` ;
